@mixin fontTitle {
    @include cardFontDescription;
    font-weight: 700;
}

@mixin cardFontDescription {
    font-size: 36px;
    line-height: 1.2;
    font-weight: 400;
}

@mixin cardTextFont {
    font-size: 17px;
    font-weight: 700;
    line-height: 1.2;
    color: #000;
}

@mixin footerText {
    font-size: 14px;
    line-height: 1.55;
}

html {
    box-sizing: border-box;
}

*, *::before, *::after {
    box-sizing: inherit;
